  describe "GET index" do
    it "assigns all playas as @playas" do
      playa = Playa.create! valid_attributes
      get :index, {}, valid_session
      assigns(:playas).should eq([playa])
    end
  end

  describe "GET show" do
    it "assigns the requested playa as @playa" do
      playa = Playa.create! valid_attributes
      get :show, {:id => playa.to_param}, valid_session
      assigns(:playa).should eq(playa)
    end
  end

  describe "GET new" do
    it "assigns a new playa as @playa" do
      get :new, {}, valid_session
      assigns(:playa).should be_a_new(Playa)
    end
  end

  describe "GET edit" do
    it "assigns the requested playa as @playa" do
      playa = Playa.create! valid_attributes
      get :edit, {:id => playa.to_param}, valid_session
      assigns(:playa).should eq(playa)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Playa" do
        expect {
          post :create, {:playa => valid_attributes}, valid_session
        }.to change(Playa, :count).by(1)
      end

      it "assigns a newly created playa as @playa" do
        post :create, {:playa => valid_attributes}, valid_session
        assigns(:playa).should be_a(Playa)
        assigns(:playa).should be_persisted
      end

      it "redirects to the created playa" do
        post :create, {:playa => valid_attributes}, valid_session
        response.should redirect_to(Playa.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved playa as @playa" do
        # Trigger the behavior that occurs when invalid params are submitted
        Playa.any_instance.stub(:save).and_return(false)
        post :create, {:playa => { "email" => "invalid value" }}, valid_session
        assigns(:playa).should be_a_new(Playa)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Playa.any_instance.stub(:save).and_return(false)
        post :create, {:playa => { "email" => "invalid value" }}, valid_session
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
