        Playa.any_instance.should_receive(:update_attributes).with({ "email" => "MyString" })
        put :update, {:id => playa.to_param, :playa => { "email" => "MyString" }}, valid_session
      end

      it "assigns the requested playa as @playa" do
        playa = Playa.create! valid_attributes
        put :update, {:id => playa.to_param, :playa => valid_attributes}, valid_session
